
public class BitwiseLogical {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 65;
		int b = 33, result = 0;
		System.out.println("a & b =" + (a&b));
		System.out.println("a|b=" + (a|b));
		System.out.println("a^b=" + (a^b));
		System.out.println("~a=" + ~a);
		
	}

}
